import React, { useState } from "react";
import { Dialog, Stack } from "@mui/material";
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogActions from '@mui/material/DialogActions';
import Typography from '@mui/material/Typography';

function EditCar(props){
    const[open,setOpen]=useState(false);
    const[car,setCar]=useState({
        brand:"",
        model:"",
        color:"",
        year:"",
        fuel:"",
        price:"",
    });
    const handleClickOpen=()=>{
        setCar({
            brand: props.data.row.brand,
            model: props.data.row.model,
            color: props.data.row.color,
            year: props.data.row.year,
            price: props.data.row.price,
        });
        setOpen(true);
    };
    const handleClose=()=>{
        setOpen(false);
    };

    const handleChange=event=>{
        setCar({...car, [event.target.name]: event.target.value});
    };
    const handleSave=()=>{
        props.updateCar(car,props.data.id);
        handleClose();
    };
    return( 
        <div>
            <button onClick={handleClickOpen}>Modifier</button>
            <Dialog open={open} onClose={handleClose}>
                <DialogTitle>Modifierla voiture</DialogTitle>
                <DialogContent>
                    <input
                        placeholder="Brand"
                        name="brand"
                        value={car.brand}
                        onChange={handleChange}
                    />
                    <br/>
                    <input
                        placeholder="Model"
                        name="model"
                        value={car.model}
                        onChange={handleChange}
                    />
                    <br/>

                    <input
                    placeholder="Color"
                    name="color"
                    value={car.color}
                    onChange={handleChange}
                />
                <br/>
                <input
                    placeholder="Year"
                    name="year"
                    value={car.year}
                    onChange={handleChange}
                />
                <br/>
                <input
                    placeholder="Price"
                    name="price"
                    value={car.price}
                    onChange={handleChange}
                />  
                <br/>
                </DialogContent>
                <DialogActions>
                    <Stack direction="row" spacing={4}>
                        <button  variant="contained" onClick={handleClose} color="blue">
                            <Typography variant="contained" style={{ color: 'blue' }}> 
                                Annuler
                            </Typography>
                        </button>
                        <button  variant="contained" onClick={handleSave} color="blue">Enregistrer</button>
                    </Stack>
                </DialogActions>
            </Dialog>
        </div>
    );
};

export default EditCar;
